sys_variable_value.value:
(function execute(inputs, outputs) {
  var email = inputs.email.split('\n');
  var first = inputs.first.split('\n');
  var last = inputs.last.split('\n');
  var code = inputs.code.split('\n');

  if (email.length == 0 || 
      first.length == 0 ||
      last.length == 0 ||
      code.length == 0) {
    outputs.records_created = 0;
  } else if (email.length === first.length && first.length === last.length && last.length === code.length){
    var records = 0;
    for (var i in email){
      var record = new GlideRecord('x_529039_communi_0_assigned_code');
      record.newRecord();
      record.setValue('email_address', email[i].trim());
      record.setValue('first_name', first[i].trim());
      record.setValue('last_name', last[i].trim());
      record.setValue('unique_code', code[i].trim());
      record.setValue('store', inputs.store);
      record.insert();
      records++;
    }
    outputs.records_created = records;
  } else {
    outputs.records_created = 0;
  }
})(inputs, outputs);

